import { useState, useContext } from "react";

import { algoContext } from "../../contexts/algoContext";


/**
 * makes a check box that represents an algorithm 
 * and updates weither the algo has been selected or not
 * 
 * @param algo string name of algorithm
 * @param index index of element in algocontext
 * @param isChecked boolean for if box is checked
 * @param handleChange Function callback funciton to update state
 * @returns checkbox with the provided algorithm name
 */


function checkbox(props: {algo:string, index:number, isChecked:boolean, handleChange:Function}){

    const algo = useContext(algoContext)

    const handleChange = () =>{
        if(props.isChecked){
            algo.sortAlgos[props.index] = {name:props.algo, active:false }
        } 
            
        else {
            algo.sortAlgos[props.index] = {name:props.algo, active:true }
        }      
        props.handleChange([...algo.sortAlgos])
    }

    return (
        <div className="">
            <input id={props.algo+"Checkbox"} type="checkbox" checked={props.isChecked} onChange={handleChange} 
                className="peer relative h-5 w-5 shrink-0 appearance-none rounded-sm border bg-white after:absolute after:left-0 after:top-0 after:h-full after:w-full after:bg-[url('data:image/svg+xml;base64,')] after:bg-[length:40px] after:bg-center after:bg-no-repeat after:content-[''] checked:bg-purple-500 hover:ring hover:ring-gray-300 focus:outline-none"
                
            />
            <label>{props.algo}</label>
        </div>
    )

}

export default checkbox